class Social < ApplicationRecord
  belongs_to :person

  validates :platform, presence: true
  validates :handle, presence: true

  enum :platform, {
    instagram: "Instagram",
    tiktok: "TikTok",
    x: "X",
    facebook: "Facebook",
    youtube: "YouTube",
    linkedin: "LinkedIn",
    other: "Other"
  }



  def url
    return nil if handle.blank?
    case platform
    when "instagram"
      "https://instagram.com/#{handle.sub(/^@/, '')}"
    when "tiktok"
      "https://tiktok.com/@#{handle.sub(/^@/, '')}"
    when "x"
      "https://x.com/#{handle.sub(/^@/, '')}"
    when "facebook"
      "https://facebook.com/#{handle.sub(/^@/, '')}"
    when "youtube"
      # Could be a channel or user, so just link to search if ambiguous
      "https://youtube.com/#{handle.sub(/^@/, '')}"
    when "linkedin"
      "https://linkedin.com/in/#{handle.sub(/^@/, '')}"
    when "other"
      handle =~ /^https?:\/\// ? handle : "https://#{handle}"
    else
      nil
    end
  end

